module Main where

    import Math.NumberTheory.GCD
    import Numeric
    import Data.Bits
    import Crypto.Number.ModArithmetic
    
    m =  :: Integer
    
    x =  :: Integer

    y =  :: Integer

    r2mp = (2^4096) `mod` m

    mp = if m' >= 0 then m' else m' + (2^2048) 
        where (gcd, m', r_inv) = extendedGCD m (2^2048)

    mgd x r m = (x * r) `mod` m
    
    mgm x y m m' = do
        let t   = x  * y
        let tm  = (t .&. (2^2048 - 1))  * m'
        let tmm = (tm .&. (2^2048 - 1)) * m
        let u   = t + tmm
        let v   = u `shiftR` 2048
        let res = if m <= v then v - m else v
        putStrLn $ showHex res ""
        return ()
        
    main = do
        --putStrLn $ showHex r2mp ""
        --putStrLn $ showHex mp ""

        putStrLn $ "Result of multiplication is:\n" ++ showHex ((x * y) `mod` m) ""

        --putStrLn $ "Result of montgomerization is: \n" ++ showHex (mgd x (2^2048) m) ""

        --mgm x r2mp m mp
        
        putStrLn $ showHex (expFast x y m) ""
